import React, { Component, useState, Fragment } from "react";
import "./CSS/App.css";
import axios from "axios";
import { Redirect, Link } from "react-router-dom";

class Login extends Component {
  constructor(props) {
    super(props);

    // this.getUser = this.getUser;
    this.state = {
      email: "",
      password: "",
      type: "department",
      isAuth: false,
      categories: [],
    };
    this.onChange = this.onChange.bind(this);
  }

  componentDidMount = async () => {
    const config = {
      headers: {
        "Content-Type": "application/json",
      },
    };
    try {
      const res = await axios.get(
        "http://localhost:5000/api/v1/category/",
        config
      );
      this.setState({
        categories: res.data.data,
      });
      console.log(res.data.data);
    } catch (err) {
      console.log("Can't load the items");
    }
  };

  // Input on change
  onChange(e) {
    this.setState({
      [e.target.name]: e.target.value,
    });
  }
  // Dropdown change
  handleDropdownChange = (e) => {
    this.setState({ categories: e.target.value });
  };
  // Login
  onSubmit = async (e) => {
    e.preventDefault();

    const login = {
      email: this.state.email,
      password: this.state.password,
    };

    const body = JSON.stringify(login);
    const config = {
      headers: {
        "Content-Type": "application/json",
      },
    };
    // console.log(body);
    try {
      const res = await axios.post(
        `http://localhost:5000/api/v1/auth/login`,
        body,
        config
      );
      console.log(res.data.token);
      sessionStorage.setItem("token", res.data.token);
      sessionStorage.setItem("isAuth", true);
      // console.log(sessionStorage);
      this.setState({
        isAuth: true,
      });
    } catch (error) {
      alert("Error Login!!");
    }
  };
  render() {
    const type = this.state.type;
    // console.log(type);
    let social = {};
    let signup;
    // if (type === "user") {
    social = "#ffc312";
    signup = <a href={`/signup/${type}`}>Sign Up</a>;

    // console.log(type);
    return (
      <Fragment>
        {this.state.isAuth ? (
          type == "department" ? (
            <Redirect isAuth={this.state.isAuth} to="/department/Home" />
          ) : (
            <Redirect isAuth={this.state.isAuth} to="/" />
          )
        ) : (
          <section id="section1">
            <div className="container logintop ">
              <div className="">
                {/* <div className="jumbotron col-md-6 col-sm-5 " id="login-first"></div> */}
                <div className=" " id="login-second">
                  <div className="container">
                    <div className="row">
                      <div className="col-lg-6 wow fadeInLeft animated">
                        <div>
                          <img
                            src="https://www.ejobspire.com/images/login.png"
                            alt=""
                            style={{ marginTop: "30px" }}
                          />
                        </div>
                      </div>
                      <div className="col-lg-6 wow fadeInUp delay-06s animated">
                        <div className="d-flex justify-content-center">
                          <div className="card animated bounce" id="login-card">
                            <div className="card-header">
                              <h3 className="mt-5"> {type} signin </h3>
                            </div>
                            <div className="card-body">
                              <form onSubmit={this.onSubmit}>
                                <div className="input-group form-group">
                                  <div className="input-group-prepend">
                                    <span
                                      className="input-group-text"
                                      style={{ background: social }}
                                    >
                                      <i className="fa fa-user"></i>
                                    </span>
                                  </div>
                                  <input
                                    type="text"
                                    className="form-control"
                                    placeholder="username"
                                    name="email"
                                    value={this.state.email}
                                    onChange={this.onChange}
                                  />
                                </div>
                                <div className="input-group form-group">
                                  <div className="input-group-prepend">
                                    <span
                                      className="input-group-text"
                                      style={{ background: social }}
                                    >
                                      <i className="fa fa-key"></i>
                                    </span>
                                  </div>
                                  <input
                                    type="password"
                                    className="form-control"
                                    placeholder="password"
                                    name="password"
                                    value={this.state.password}
                                    onChange={this.onChange}
                                  />
                                </div>
                                <div className="form-row frow">
                                  <div className="name col-md-5">
                                    Department:
                                  </div>

                                  <select
                                    id="dropdown "
                                    className="btn bg-light col-md-6 form-control"
                                    onChange={this.handleDropdownChange}
                                  >
                                    <option value="no cat">None</option>
                                    {this.state.categories.map((cat) => (
                                      <option key={cat._id} value={cat._id}>
                                        {cat.catname}
                                      </option>
                                    ))}
                                  </select>
                                </div>
                                <div className="form-group">
                                  <button
                                    type="submit"
                                    value="Login"
                                    name="submit"
                                    className="btn float-right login_btn btn-block "
                                    style={{
                                      backgroundColor: social,
                                    }}
                                  >
                                    Login
                                  </button>
                                </div>
                              </form>
                            </div>
                            <ul className="social-link">
                              <li>
                                <a href="(0)">
                                  <i className="fa fa-twitter "></i>
                                </a>
                              </li>
                              <li className="facebook animated bounceIn wow delay-03s animated">
                                <a href="(0)">
                                  <i className="fa fa-facebook "></i>
                                </a>
                              </li>
                              <li className="pinterest animated bounceIn wow delay-04s animated">
                                <a href="(0)">
                                  <i className="fa fa-pinterest "></i>
                                </a>
                              </li>
                              <li className="dribbble animated bounceIn wow delay-06s animated">
                                <a href="(0)">
                                  <i className="fa fa-instagram "></i>
                                </a>
                              </li>
                            </ul>
                            <div className="card-footer">
                              <div className="d-flex justify-content-center links">
                                Don't have an account?
                                {signup}
                              </div>
                              <div className="d-flex justify-content-center">
                                <a href="/reset">Forgot your password?</a>
                                <a href="/main/Home">Admin</a>
                              </div>
                            </div>
                          </div>
                        </div>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </section>
        )}
      </Fragment>
    );
  }
}

export default Login;
