import React, { Component } from "react";
import "../CSS/donor.css";

export default class addDonors extends Component {
  constructor(props) {
    super(props);

    this.state = {
      category: [],
      title: "",
      description: "",
      stock: "",
      rate: "",
      category: "",
      file: null,
    };
    this.onChange = this.onChange.bind(this);
    this.handleDropdownChange = this.handleDropdownChange.bind(this);
  }
  componentDidMount() {
    this.props.getCategory();
  }
  // Input on change
  onChange(e) {
    this.setState({
      [e.target.name]: e.target.value,
    });
  }
  // Dropdown change
  handleDropdownChange(e) {
    this.setState({ category: e.target.value });
  }
  // fileupload
  onChangeHandler = (e) => {
    this.setState({
      file: e.target.files[0],
    });
  };
  onSubmit = async (e) => {
    e.preventDefault();
    const data = new FormData();
    data.append("file", this.state.file, this.state.file.name);

    // console.log(data);
    const token = sessionStorage.getItem("token");
    const config = {
      headers: {
        Authorization: `Bearer ${token}`,
        "Content-Type": "multipart/form-data",
      },
    };
    try {
      const res = await axios.post(
        `http://localhost:5000/api/v1/vendors/products/photo`,
        data,
        config
      );
      console.log(res.data.data);

      const products = {
        title: this.state.title,
        description: this.state.description,
        category: this.state.category,
        rate: this.state.rate,
        stock: this.state.stock,
        photo: res.data.data,
      };
      const body = JSON.stringify(products);
      console.log(body);
      const config1 = {
        headers: {
          Authorization: `Bearer ${token}`,
          "Content-Type": "application/json",
        },
      };
      const result = await axios.post(
        `http://localhost:5000/api/v1/vendors/products`,
        body,
        config1
      );
      console.log(result.data.data);
      alert(`Product Added ${result.data.data.title}`);
    } catch (err) {
      // console.log("Can't load the items");
    }
  };
  render() {
    return (
      <section id="section2">
        <div className="container itmtop">
          <div className="">
            {/* <div className="jumbotron col-md-6 col-sm-5 " id="login-first"></div> */}
            <div className="" id="login-second">
              <div class="page-wrapper p-t-50 p-b-50">
                <div class="wrapper wrapper--w900 ">
                  <div class="card cardH card-6 ">
                    <div class="card-heading m-4">
                      <h2 class="title text-dark">Add Complaints</h2>
                    </div>
                    <div class="card-body">
                      <form
                        onSubmit={this.onSubmit}
                        encType="multipart/form-data"
                      >
                        <div class="form-row frow">
                          <div class="name">Complaints Title:</div>
                          <div class="value">
                            <input
                              class="input--style-6"
                              type="text"
                              name="title"
                              value={this.state.title}
                              onChange={this.onChange}
                            />
                          </div>
                        </div>
                        <div class="form-row frow">
                          <div class="name">Complaints Description:</div>
                          <div class="value">
                            <input
                              class="input--style-6"
                              type="text"
                              name="description"
                              placeholder=""
                              value={this.state.description}
                              onChange={this.onChange}
                            />
                          </div>
                        </div>

                        {/* <div class="form-row frow">
                          <div class="name">Department</div>
                          <div class="value">
                            <div class="input-group">
                              <input
                                class="input--style-6"
                                type="text"
                                name="desc"
                                placeholder=""
                              />
                            </div>
                          </div>
                        </div> */}
                        <div className="form-row frow">
                          <div className="name">Department:</div>

                          <select
                            id="dropdown "
                            className="btn bg-success"
                            onChange={this.handleDropdownChange}
                          >
                            <option value="no cat">None</option>
                            {this.props.category.map((category) => (
                              <option key={category._id} value={category._id}>
                                {category.catname}
                              </option>
                            ))}
                          </select>
                        </div>

                        <div class="form-row frow">
                          <div class="name">Upload Image:</div>
                          <div class="value">
                            <div class="input-group js-input-file">
                              <input
                                className="input-file"
                                type="file"
                                name="file"
                                id="file"
                                onChange={this.onChangeHandler}
                              />
                              <label class="label--file" for="file">
                                Choose file
                              </label>
                            </div>
                            <div class="label--desc">
                              Upload your Document/Id proff or any other
                              relevant file. Max file size 50 MB
                            </div>
                          </div>
                        </div>

                        {/* <div class="form-row frow">
                          <div class="name">Complaints name</div>
                          <div class="value">
                            <div class="input-group">
                              <input
                                class="input--style-6"
                                type="email"
                                name="email"
                                placeholder=""
                              />
                            </div>
                          </div>
                        </div> */}
                        <div class="form-row frow">
                          <div class="name">Ward Number</div>
                          <div class="value">
                            <div class="input-group">
                              <input
                                class="input--style-6"
                                type="text"
                                name="ward"
                                placeholder=""
                                value={this.state.ward}
                                onChange={this.onChange}
                              />
                            </div>
                          </div>
                        </div>
                        <div class="form-row frow">
                          <div class="name">Contact</div>
                          <div class="value">
                            <div class="input-group">
                              <input
                                class="input--style-6"
                                type="text"
                                name="phone"
                                placeholder=""
                                value={this.state.phone}
                                onChange={this.onChange}
                              />
                            </div>
                          </div>
                        </div>
                        <div class="form-row frow">
                          <div class="name">Email</div>
                          <div class="value">
                            <div class="input-group">
                              <input
                                class="input--style-6"
                                type="email"
                                name="email"
                                placeholder=""
                                value={this.state.email}
                                onChange={this.onChange}
                              />
                            </div>
                          </div>
                        </div>
                        <div class="form-row frow">
                          <div class="name">Address</div>
                          <div class="value">
                            <div class="input-group">
                              <input
                                class="input--style-6"
                                type="email"
                                name="email"
                                placeholder=""
                              />
                            </div>
                          </div>
                        </div>
                        <div class="form-row frow">
                          <div class="name">Facing Issue Form</div>
                          <div class="value">
                            <div class="input-group">
                              <input
                                class="input--style-6"
                                type="date"
                                name="email"
                                placeholder=""
                              />
                            </div>
                          </div>
                        </div>
                      </form>
                    </div>
                    <div class="card-footer">
                      <button class="btn btn--radius-2 btn-gray" type="submit">
                        ADD
                      </button>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </section>
    );
  }
}
